package Q1;

public class AddTwoIntegers {
	public static int add(int a, int b) {
        return a + b;
        }

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 6;
        int num2 = 4;
        int sum = add(num1, num2);
        System.out.println("Sum of " + num1 + " and " + num2 + " is: " + sum);

	}

}

